import React from "react";
import { useNavigate } from "react-router-dom";

export default class AreasView extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      areas: [],
    };
  }

  componentDidMount() {
    fetch("http://localhost/VSV/SAT/API/areas.php")
      .then((res) => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            areas: result.list,
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error: "Error loading areas",
          });
        }
      );
  }

  render() {
    return (
      <div>
        {/*Header Bar With Search*/}
        <div className="areas-header">
          <span className="header networkingDeviceList">
            <span>
              <span>Areas</span>
              <span className="header-badge">38</span>
            </span>
            <input
              type="text"
              className="devicesSearch"
              placeholder="Search Areas"
              data-bind="textInput: deviceSearchQuery, event:{ change: $root.filterNetworkingDevices}"
            />
          </span>
        </div>
        {/*Area Cards */}
        <div className="mdc-layout-grid cards-areas-grid">
          <div className="mdc-layout-grid__inner">
            {this.state.areas.map((area) => (
              <div className="mdc-layout-grid__cell mdc-layout-grid__cell--span-4">
                <div className="mdc-card">
                  <div className="mdc-card__primary-action">
                    <div className="mdc-card__media mdc-card__media--square">
                      <img
                        src="https://eatsleepworkrepeat.com/wp-content/uploads/2020/06/office.jpg"
                        alt="area-image"
                      />
                    </div>
                    <div className="mdc-card__primary">
                      <h2 className="mdc-card__title mdc-card__title--large">
                        {area.name}
                      </h2>
                      <h3 className="mdc-card__subtitle">{area.description}</h3>
                    </div>
                  </div>
                  <div className="mdc-card__actions">
                    <div className="mdc-card__action-buttons">
                      <button className="mdc-button mdc-card__action mdc-card__action--button">
                        <span className="mdc-button__label">View</span>
                      </button>
                      <button className="mdc-button mdc-card__action mdc-card__action--button">
                        <span className="mdc-button__label">Edit</span>
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

function AreaCard(props) {
  const navigate = useNavigate();
  const entity = props.entity;

  function handleClick() {
    navigate("/areas/" + entity.DEVICE_ID);
  }

  return (
    <tr onClick={handleClick}>
      <td {...{ "td-online-badge": "somevalue" }}>{entity.IP_ADDRESS}</td>
      <td>{entity.NAME}</td>
      <td>{entity.LOCATION}</td>
      <td>{entity.NOTES}</td>
    </tr>
  );
}
