import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Outlet,
  Link,
  BrowserRouter,
  useLocation,
} from "react-router-dom";

class NavBar extends React.Component {
  render() {
    return (
      <aside className="mdc-drawer mdc-drawer--dismissible mdc-drawer--open">
        <div className="mdc-drawer__header">
          <h3 className="mdc-drawer__title">Devices Dashboard</h3>
          <h6 className="mdc-drawer__subtitle">Test</h6>
        </div>
        <div className="mdc-drawer__content">
          <div className="mdc-list">
            {/* Bottom Navigation */}
            <a
              className="mdc-list-item bottomTabsForNav"
              href="../API/logout.php"
            >
              <i
                className="material-icons mdc-list-item__graphic"
                aria-hidden="true"
              >
                logout
              </i>
              <span className="mdc-list-item__text">Logout</span>
            </a>
          </div>
        </div>
      </aside>
    );
  }
}

function Layout() {
  return (
    <div>
      <NavBarItem to="/" name={"Home"} icon={"home"} />
      <NavBarItem to="/areas" name={"Areas"} icon={"room"} />
      <NavBarItem to="/devices" name={"Devices"} icon={"settings_ethernet"} />
        <Outlet />
    </div>
  );
}

function NavBarItem({ name, icon, to }) {
  const location = useLocation();
  const match = location.pathname === to;
  const classNames =
    "mdc-list-item" + (match ? " mdc-list-item--activated" : "");

  return (
    <Link className={classNames} to={to}>
      <i className="material-icons mdc-list-item__graphic" aria-hidden="true">
        {icon}
      </i>
      <span className="mdc-list-item__text"> {name} </span>
    </Link>
  );
}

function Home() {
  return (
    <div>
      <h2 style={{ float: "right" }}>Home</h2>
    </div>
  );
}

function About() {
  return (
    <div>
      <h2>About</h2>
    </div>
  );
}

function Dashboard() {
  return (
    <div>
      <h2>Dashboard</h2>
    </div>
  );
}

function NoMatch() {
  return (
    <div>
      <h2>Nothing to see here!</h2>
      <p>
        <Link to="/">Go to the home page</Link>
      </p>
    </div>
  );
}

export default NavBar;
