import React, {useEffect, useState} from "react";
import EntitySideDetail from "../components/entitySideDetail";
import { Routes, Route, useParams } from "react-router-dom";

export default function EntityView(props) {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);

    let params = useParams();
    let entityId = params.id;

    // Note: the empty deps array [] means
    // this useEffect will run once
    // similar to componentDidMount()
    useEffect(() => {
          fetch(`http://localhost/VSV/SAT/API/getEntity.php?id=${entityId}`)
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setItems(result);
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])

    if (error) {
        return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
        return <div>Loading...</div>;
    } else {
        return (
            <div>
                {/*Header Bar */}
                <div className="areas-header">
          <span className="header networkingDeviceList">
            <span>
              <span>Title </span>
              <span className="header-badge">Sub</span>
            </span>
          </span>
                </div>
                {/*Main Content*/}
                <div className="table-wrapper">
                    {/*Side Detail */}
                    <EntitySideDetail entityId={'this.props.match.params.id'} />
                    {/*Main Detail */}
                </div>
            </div>
        );
    }
}
