import React from "react";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

export const options = {
  responsive: true,
  plugins: {
    legend: {
      position: "top",
    },
    title: {
      display: false,
    },
  },
  scales: { y: { max: 105, min: 0, ticks: { beginAtZero: true } } },
};

export default class UptimeChart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      uptimeChart: {
        data: {
          labels: [
            "Loading...",
              "Loading...",
              "Loading...",
              "Loading...",
              "Loading...",
              "Loading...",
              "Loading...",
              "Loading...",
              "Loading...",
              "Loading...",
              "Loading...",
              "Loading...",
          ],
          datasets: [
            {
              label: "Uptime",
              data: [
                0,
              ],
              borderColor: "green",
              backgroundColor: "green",
            },
          ],
        },
      },
    };
  }

  render() {
    return (
      <div>
        <h5>
          <span>Uptime</span>
          <a
            className="link"
            style={{ float: "right" }}
            data-bind="text: $root.deviceSelectedChartPeriod, click: $root.toggle_NetworkingDeviceSelectedChartPeriod"
          >
            Hourly
          </a>
        </h5>

        <Line
          options={options}
          data={this.state.uptimeChart.data}
          width={"656px"}
          height={"328px"}
          style={{
            display: "block",
            boxSizing: "border-box",
            height: "164px",
            width: "328px",
          }}
        />
      </div>
    );
  }
}
