import React from "react";

export default class EntitiesView extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            entities: []
        };
    }

    componentDidMount() {
        fetch("http://localhost/VSV/SAT/API/devices.php")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        entities: result.list
                    });
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error: 'Error loading entities'
                    });
                }
            )
    }

  render() {
    return (
      <div>
        {/* Header Bar With Search */}
        <div className="areas-header">
          <span className="header networkingDeviceList">
            <span>
              <span>Page Title </span>
              <span className="header-badge">4 Areas</span>
            </span>
            <input
              type="text"
              className="devicesSearch"
              placeholder="Search"
              data-bind="textInput: deviceSearchQuery, event:{ change: $root.filterNetworkingDevices}"
              style={{ marginRight: 16 }}
            />
          </span>
        </div>
        {/*Devices Table*/}
        <div className="table-wrapper">
          <table className="fancy-table">
            <thead>
              <tr>
                <th>IP</th>
                <th>Name</th>
                <th>LOCATION</th>
                <th>Notes</th>
              </tr>
            </thead>
            <tbody>
            {this.state.entities.map(entity => (
                <tr key={entity.DEVICE_ID}>
                  <td>{entity.ip}</td>
                  <td>{entity.name}</td>
                  <td>{entity.location}</td>
                  <td>{entity.notes}</td>
                </tr>
              ))}
              {/*<tr data-bind="click: $root.selectDevice">*/}
              {/*    <td class="class-code underlined" data-bind="text: IP_ADDRESS" <?php echo $statusAttributes[0] ?> ></td>*/}
              {/*<td class="class-code" data-bind="text: NAME || DEVICE_TYPE || '' "></td>*/}
              {/*<td class="enrol-count" data-bind="text:  LOCATION || '.' "></td>*/}
              {/*<td class="enrol-count" data-bind="text: NOTES || '.' "></td>*/}
              {/*</tr>*/}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}
