import React from "react";
import { Autosave, useAutosave } from 'react-autosave';
import {useEffect, useState} from "@types/react";
import {useParams} from "react-router-dom";
import EntityFieldGroups from "./entityFieldGroups";
import EntitySideDetail from "./entitySideDetail";

export default class EntityField extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    return (
      <div className="entity-field" key={this.props.field.htmlId}>
        <td>
          <span id="field-label-status-{{this.props.field.htmlId}}">
            {this.props.field.label}
          </span>
        </td>
        <td>
          {this.props.field.type === "select" ? (
            <EntityField_Select field={this.props.field} />
          ) : (
            <EntityField_Common field={this.props.field} />
          )}
        </td>
      </div>
    );
  }
}

class EntityField_Select extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render() {
    return (
      <select
        id={this.props.field.htmlId}
        style={{
          marginLeft: "inherit",
        }}
      >
        <option value="f">Select</option>
        <option value="dd">Front Balcony</option>
        <option value="dd">Front Hdff</option>
      </select>
    );
  }
}

function EntityField_Common(props) {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [entity, setEntity] = useState([]);

  let params = useParams();
  let entityId = params.id;

  // Note: the empty deps array [] means
  // this useEffect will run once
  // similar to componentDidMount()
  useEffect(() => {
    fetch(`http://localhost/VSV/SAT/API/getEntity.php?id=${entityId}`)
        .then((res) => res.json())
        .then(
            (result) => {
              setIsLoaded(true);
              setEntity(result);
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              setIsLoaded(true);
              setError(error);
            }
        );
  }, []);

  return (
      <input
          id={props.field.htmlId}
          type={props.field.type}
          value={props.field.value}
          placeholder={props.field.placeholder}
      />
  );
}
